import React from 'react';
import './Home.css'
import {Link} from 'react-router-dom'
class Home extends React.Component{

    componentDidMount(props){
      var TxtRotate = function(el, toRotate, period) {
          this.toRotate = toRotate;
          this.el = el;
          this.loopNum = 0;
          this.period = parseInt(period, 10) || 2000;
          this.txt = '';
          this.tick();
          this.isDeleting = false;
        };
          
      TxtRotate.prototype.tick = function() {
        var i = this.loopNum % this.toRotate.length;
        var fullTxt = this.toRotate[i];
      
        if (this.isDeleting) {
          this.txt = fullTxt.substring(0, this.txt.length - 1);
        } else {
          this.txt = fullTxt.substring(0, this.txt.length + 1);
        }
      
        this.el.innerHTML = '<span class="wrap">'+this.txt+'</span>';
      
        var that = this;
        var delta = 150 - Math.random() * 100;
      
        if (this.isDeleting) { delta /= 2; }
      
        if (!this.isDeleting && this.txt === fullTxt) {
          delta = this.period;
          this.isDeleting = true;
        } else if (this.isDeleting && this.txt === '') {
          this.isDeleting = false;
          this.loopNum++;
          delta = 300;
        }
      
        setTimeout(function() {
          that.tick();
        }, delta);
      };
        
      var driver = function() {
        var elements = document.getElementsByClassName('txt-rotate');
        for (var i=0; i<elements.length; i++) {
          var toRotate = elements[i].getAttribute('data-rotate');
          var period = elements[i].getAttribute('data-period');
          if (toRotate) {
            new TxtRotate(elements[i], JSON.parse(toRotate), period);
          }
        }
        // INJECT CSS
        var css = document.createElement("style");
        css.type = "text/css";
        css.innerHTML = ".txt-rotate > .wrap { border-right: 0.08em solid #666 }";
        document.body.appendChild(css);
      };
      driver()
    }
    render(){
        return(
            <div class="home-div">
                <div class="home-title-div"> Daniel Yu</div>
                <div class="home-subtitle-div"> I am a
                  <span class="txt-rotate" data-period="2000" data-rotate='[ " developer", " mathematician", " designer", " student"]'>
                  </span>
                </div>
                <div class="button_cont" align="center">
                    <Link to="/about"class="example_c" >Learn More</Link>
                </div>

            </div>
        )
    }
}

export default Home
